const App = () => {

  const course = 'Half Stack application development';

  const part1 = 'Fundamentals of React';
  const exercises1 = 10;

  const part2 = 'Using props to pass data';
  const exercises2 = 7;

  const part3 = 'State of a component';
  const exercises3 = 14;

  const Header = (props) => {
    return (
      <h1>{props.course}</h1>
    );
  }

  const Content = (props) => {
    return (
      <>{props.part}</>
    );
  }

  const Total = (props) => {
    return (
      <p>Number of exercises {props.total}</p>
    );
  }

  return (
    <div>
      <Header course={course} />
      <Content part={part1}/>
      <Total total={exercises1 + exercises2 + exercises3}/>
      <p>
        {part2} {exercises2}
      </p>
      <p>
        {part3} {exercises3}
      </p>
    </div>
  );
}

export default App
